  "explanation_rich": [
    {
      "type": "paragraph",
      "children": [
        {
          "text": "The union node permits providing a constant when a source lacks a needed column, enabling consistent output columns."
        }
      ]
    }
  ],
  "elaboration_prompts_rich": [
    {
      "type": "paragraph",
      "children": [
        {
          "text": "Describe a case where one source lacks a column and how a constant ensures a uniform schema."
        }
      ]
    }
  ],
  "shuffle": false,
  "grading": {
    "mode": "mcq",
    "partial_credit": false,
    "penalty": 0,
    "require_all_correct": false
  },
  "example_rich": [
    {
      "type": "paragraph",
      "children": [
        {
          "text": "If Source A has Country and Source B does not, assign a constant like 'N/A' for Country in Source B."
        }
      ]
    }
  ]
}

