{
  "schema_version": "1.0",
  "units": [
    {
      "unit_id": "u1",
      "title": "Unit 1 – Preparing the Modeling Environment",
      "topics": [
        {
          "topic_id": "u1_t1_intro-bas-hc",
          "title": "Getting Started with BAS for HANA Cloud",
          "summary": "Overview of the unit outcomes and BAS’s role in SAP HANA Cloud modeling.",
          "chunk_span": [0, 0]
        },
        {
          "topic_id": "u1_t2_dev-spaces",
          "title": "Development Spaces and Extensions",
          "summary": "Create and manage BAS development spaces and select required extensions like the Calculation View Editor.",
          "chunk_span": [1, 1]
        },
        {
          "topic_id": "u1_t3_import-git",
          "title": "Importing Projects and Using Git",
          "summary": "Import existing projects into the workspace and use Git for sharing and collaboration.",
          "chunk_span": [2, 2]
        },
        {
          "topic_id": "u1_t4_unit-quiz",
          "title": "Unit Knowledge Check",
          "summary": "Quick quiz reinforcing project import and collaboration choices.",
          "chunk_span": [3, 3]
        }
      ]
    },
    {
      "unit_id": "u2",
      "title": "Unit 2 – Core Modeling Concepts and View Types",
      "topics": [
        {
          "topic_id": "u2_t1_concepts",
          "title": "Modeling Terminology and Key Concepts",
          "summary": "Define measures and attributes and foundational modeling terms in SAP HANA Cloud.",
          "chunk_span": [4, 4]
        },
        {
          "topic_id": "u2_t2_dimension-views",
          "title": "Creating Dimension Calculation Views",
          "summary": "Build reusable master-data views with attributes, filters, and hierarchies.",
          "chunk_span": [5, 5]
        },
        {
          "topic_id": "u2_t3_cube-views",
          "title": "Creating Cube Calculation Views",
          "summary": "Create measure-based cubes with automatic aggregation by requested attributes.",
          "chunk_span": [6, 6]
        },
        {
          "topic_id": "u2_t4_time-dimensions",
          "title": "Time-Based Dimension Views",
          "summary": "Generate date-derived attributes from a base date using time dimensions.",
          "chunk_span": [7, 7]
        }
      ]
    },
    {
      "unit_id": "u3",
      "title": "Unit 3 – Building, Validating, and Semantics",
      "topics": [
        {
          "topic_id": "u3_t1_data-preview",
          "title": "Checking Calculation View Output",
          "summary": "Use Data Preview in BAS to validate calculation view results.",
          "chunk_span": [8, 8]
        },
        {
          "topic_id": "u3_t2_common-features",
          "title": "Common Features and Column Semantics",
          "summary": "Configure the Semantics node, assign column types, and define behavior for consumers.",
          "chunk_span": [9, 9]
        },
        {
          "topic_id": "u3_t3_top-node",
          "title": "Understanding the Top View (Semantics) Node",
          "summary": "Explain the mandatory Semantics top node and its enrichment capabilities.",
          "chunk_span": [10, 10]
        }
      ]
    }
  ]
}
